// src/pages/StudentManagement.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentManagement = () => {
  const [students, setStudents] = useState([]);
  const [editingStudent, setEditingStudent] = useState(null);
  const [newStudent, setNewStudent] = useState({ name: '', email: '' });

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('/api/students'); // Replace with your API endpoint
      setStudents(response.data);
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const handleEdit = (student) => {
    setEditingStudent(student);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/students/${id}`); // Replace with your API endpoint
      fetchStudents();
    } catch (error) {
      console.error('Error deleting student:', error);
    }
  };

  const handleUpdate = async (event) => {
    event.preventDefault();
    try {
      await axios.put(`/api/students/${editingStudent.id}`, editingStudent); // Replace with your API endpoint
      fetchStudents();
      setEditingStudent(null);
    } catch (error) {
      console.error('Error updating student:', error);
    }
  };

  const handleAddStudent = async (event) => {
    event.preventDefault();
    try {
      await axios.post('/api/students', newStudent); // Replace with your API endpoint
      fetchStudents();
      setNewStudent({ name: '', email: '' });
    } catch (error) {
      console.error('Error adding student:', error);
    }
  };

  return (
    <div>
      <h2>Student Management</h2>
      <form onSubmit={handleAddStudent}>
        <input
          type="text"
          placeholder="Name"
          value={newStudent.name}
          onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
        />
        <input
          type="email"
          placeholder="Email"
          value={newStudent.email}
          onChange={(e) => setNewStudent({ ...newStudent, email: e.target.value })}
        />
        <button type="submit">Add Student</button>
      </form>

      {editingStudent && (
        <form onSubmit={handleUpdate}>
          <input
            type="text"
            value={editingStudent.name}
            onChange={(e) => setEditingStudent({ ...editingStudent, name: e.target.value })}
          />
          <input
            type="email"
            value={editingStudent.email}
            onChange={(e) => setEditingStudent({ ...editingStudent, email: e.target.value })}
          />
          <button type="submit">Update Student</button>
        </form>
      )}

      <ul>
        {students.map(student => (
          <li key={student.id}>
            {student.name} ({student.email})
            <button onClick={() => handleEdit(student)}>Edit</button>
            <button onClick={() => handleDelete(student.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StudentManagement;
