import React from "react";
import StickyNavbar from "./components/Navbar";
import HomePage from "./pages/Home";
import Dashboard from "./pages/Dashboard";
import About from "./pages/About";
import Registration from "./pages/Registration";
import Login from "./pages/Login"; // Import the Login page
import Logout from "./components/Logout"; // Import the Logout component
import { BrowserRouter as Router, Route, Routes,Navigate } from "react-router-dom";

import { AuthProvider, AuthContext } from "../src/contexts/AuthContext"; // Import the AuthProvider and AuthContext





function App() {
  const { auth } = useContext(AuthContext); // Use authentication state
  return (
    <Router>
      <div>
        <StickyNavbar />
        {auth && <Logout />} {/* Show Logout button if authenticated */}
        <Routes>
          
          <Route path="/" element={<HomePage />} />
          <Route path="/dashboard" element={auth ? <Dashboard /> : <Navigate to="/login" />} />
          {/*<Route path="/dashboard" element={<Dashboard />} />*/}
          <Route path="/about" element={<About/>} />
          <Route path="/registration" element={<Registration />}/>
          <Route path="/login" element={!auth ? <Login /> : <Navigate to="/dashboard" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
