import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import './Navbar.css';

const Navbar = () => {
  const { isAuthenticated, logout } = useContext(AuthContext);
  const [isOpen, setIsOpen] = React.useState(false);

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <nav className="navbar">
      <button onClick={toggleMenu} className="navbar-toggle">
        â˜°
      </button>
      <div className={`navbar-menu ${isOpen ? 'active' : ''}`}>
        {isAuthenticated ? (
          <>
           <a href="/" className="navbar-item">Home</a>
          <a href="/dashboard" className="navbar-item">Dashboard</a>
       

          <a href="/registration" className="navbar-item">Register</a>
          <a href="/about" className="navbar-item">About</a>
            <button onClick={logout} className="navbar-item">Logout</button>
          </>
        ) : (
          <>
          
            <Link to="/login" className="navbar-item">Login</Link>
           
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
