// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import './Navbar.css';
import Logo from '../assets/nitLogo.png';
const handleRegistration = () => {
  if (!name || !rollNo || !hostel || !year || !selectedSlot) {
  
    toast.error('Please fill all fields');
    return;
  }

  if (checkins[rollNo]) {
  
    toast.error('Already registered');
  } else {
    register(name, rollNo, hostel, year, selectedSlot);
    const newCheckins = {
      ...checkins,
      [rollNo]: { name, hostel, year, slot: selectedSlot, checkIn: null, checkOut: null },
    };
    setCheckins(newCheckins);
   
    toast.success('Registration successful');
    
    // Clear the inputs
    setName('');
    setRollNo('');
    setHostel('');
    setYear('');
    setSelectedSlot('');
  }
};
const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const { isAuthenticated, logout } = useAuth();

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <a href="/" className="navbar-logo">
          <img src={Logo} alt="Logo" className="navbar-logo" />
        </a>
        <h1>
          राष्ट्रीय प्रौद्योगिकी संस्थान हमीरपुर <br /> National Institute of Technology Hamirpur
        </h1>

        <div className={`navbar-menu ${isOpen ? 'active' : ''}`}>
          <Link to="/" className="navbar-item">Home</Link>
          {isAuthenticated ? (
            <>
              <Link to="/dashboard" className="navbar-item">Dashboard</Link>
              <Link to="/registration" className="navbar-item">Register</Link>
              <Link to="/about" className="navbar-item" >About</Link>
              <div onClick={logout} className="navbar-item logout" >Import</div>
              <div onClick={logout} className="navbar-item logout" >Logout</div>
            </>
          ) : (
            <>
             <Link to="/login" className="navbar-item logout " >Login</Link>
            </>
          )}
        </div>
        <div className="navbar-toggle" onClick={toggleMenu}>
          {isOpen ? (
            <svg xmlns="http://www.w3.org/2000/svg" className="icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
          ) : (
            <svg xmlns="http://www.w3.org/2000/svg" className="icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path strokeLinecap="round" strokeLinejoin="round" d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
