import React, { useState } from "react";
import { useCheckin } from "../contexts/CheckinContext";
import { Link } from "react-router-dom";
import axios from "axios";
import './Home.css';

const HomePage = () => {
  const { checkins, setCheckins } = useCheckin();
  const [rollNo, setRollNo] = useState('');
  const [message, setMessage] = useState('');

  // Function to handle check-in and check-out
  const handleCheckInOut = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/checkin", { rollNo });
      console.log(response.data);
      setMessage(response.data.message);
      alert(response.data.message);
      
      // Update local state with check-in/check-out information if necessary
      if (response.data.checkin) {
        const newCheckins = {
          ...checkins,
          [rollNo]: response.data.checkin,
        };
        setCheckins(newCheckins);
      }
      
      setRollNo(''); // Clear input field after operation
    } catch (error) {
      console.error(error);
      setMessage(error.response.data.message);
      alert(error.response.data.message);
    }
  };

  return (
    <div className="body">
      <div className="home-container">
        <h1>Gym Check-In/Check-Out</h1>
        <input
          type="text"
          value={rollNo}
          onChange={(e) => setRollNo(e.target.value)}
          placeholder="Enter Roll Number"
        />
        {rollNo && <button onClick={handleCheckInOut}>Check In/Out</button>}
        <p>{message}</p>
        
        <nav>
          <Link to="/dashboard">Go to Dashboard</Link>
        </nav>
      </div>
    </div>
  );
};

export default HomePage;
