const HomePage = () => {
  const { checkins, setCheckins } = useCheckin();
  const [rollNo, setRollNo] = useState('');
  const [message, setMessage] = useState('');

  async function checkin(rollNo) {
    await axios.post("http://localhost:5000/api/checkin", { rollNo })
      .then((response) => {
        setMessage(response.data.message);
        alert(response.data.message);
      })
      .catch((error) => {
        setMessage(error.response.data.message);
        alert(error.response.data.message);
      });
  }

  const getCurrentTimeInIST = () => {
    const date = new Date();
    const utc = date.getTime() + (date.getTimezoneOffset() * 60000);
    return new Date(utc + (3600000 * 5.5));
  };

  const handleCheckInOut = () => {
    if (rollNo) {
      checkin(rollNo);
    } else {
      setMessage('Please enter your roll number.');
      alert('Please enter your roll number.');
    }
  };

  return (
    <div className="body">
      <div className="home-container">
        <h1>Gym Check-In/Check-Out</h1>
        <input
          type="text"
          value={rollNo}
          onChange={(e) => setRollNo(e.target.value)}
          placeholder="Enter Roll Number"
        />
        <button onClick={handleCheckInOut}>Check In/Out</button>
        <p>{message}</p>
        <nav>
          <Link to="/dashboard">Go to Dashboard</Link>
        </nav>
      </div>
    </div>
  );
};
