import React, { useState } from 'react';
import { useCheckin } from '../contexts/CheckinContext';
import { Link } from 'react-router-dom';
import axios from "axios";
import './Home.css'; 

const HomePage = () => {
  const { checkins, setCheckins } = useCheckin();
  const [rollNo, setRollNo] = useState('');
  const [message, setMessage] = useState('');

  const handleCheckInOut = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/checkin", { rollNo });
      const { data } = response;
      setMessage(data.message);
      
      // Update check-ins state based on server response
      if (data.checkInTime) {
        setCheckins({
          ...checkins,
          [rollNo]: { checkIn: data.checkInTime, slot: data.slot },
        });
      } else if (data.checkOutTime) {
        setCheckins({
          ...checkins,
          [rollNo]: { ...checkins[rollNo], checkOut: data.checkOutTime },
        });
      }
      
      alert(data.message); // Show popup alert
    } catch (error) {
      console.log(error);
      setMessage(error.response?.data?.message || 'An error occurred');
      alert(error.response?.data?.message || 'An error occurred');
    }
    setRollNo(''); // Clear input field
  };

  return (
    <div className="body">
      <div className="home-container">
        <h1>Gym Check-In/Check-Out</h1>
        <input
          type="text"
          value={rollNo}
          onChange={(e) => setRollNo(e.target.value)}
          placeholder="Enter Roll Number"
        />
        {rollNo && <button onClick={handleCheckInOut}>Check In/Out</button>}
        <p>{message}</p>
        <nav>
          <Link to="/dashboard">Go to Dashboard</Link>
        </nav>
      </div>
    </div>
  );
};

export default HomePage;
